import React, { Component } from 'react';
import {CardList} from "./components/cards/Cards";
import Filter from "./components/cards/Filter";

class App extends Component {
  state = {
    cards: [
      { name: "Aruna Singh",
        "avatar_url": "https://avatars3.githubusercontent.com/u/17371652",
         "company": "Ex2 Soulution" 
      },
      { name: "Simonjefford",
        "avatar_url": "https://avatars2.githubusercontent.com/u/136?v=4",
         "company": "ABC Company" 
      },
      { name: "Josh",
        "avatar_url": "https://avatars2.githubusercontent.com/u/137?v=4",
         "company": "XYZ Company" 
      },
      { name: "stevedekorte",
        "avatar_url": "https://avatars3.githubusercontent.com/u/138?v=4",
         "company": "pqa Soulution" 
      },
  ]
  }  



  render() {

    return (
      <div className="card-wrapper">
         <Filter></Filter>
        <CardList cards={this.state.cards}></CardList>
      </div>
    );
  }
}

export default App;
